import React, { useState } from 'react';
var countTrees=200;
const IncreaseTree=(tree)=>{
	countTrees=(tree+countTrees)
}
function TreeCounter() {
	
	
	IncreaseTree()
	const count_css = {
		fontSize: '150px', 
		fontWeight: '1000',
	}

	const x_css = {
		fontSize: '80px',
		fontWeight: '1000',
		display: 'block', 
		margin: '0 0 100px 0',
		color: 'rgb(111, 186, 13)'
	}
	
	return (
		<div className="tree-counter">
			<span style={count_css}>{countTrees}</span>
			<span style={x_css}>trees planted</span>
		</div>
	);

}

export default TreeCounter;
export {countTrees,IncreaseTree};
