import React, { useState, useEffect } from 'react';
import './Navbar.css'; // Import your CSS for styling

function Navbar() {
	const [isNavbarOpen, setIsNavbarOpen] = useState(false);
	const [activeSection, setActiveSection] = useState('scrolltop');

	const toggleNavbar = () => {
		setIsNavbarOpen(!isNavbarOpen);
	};

	const handleScroll = () => {
		const scrollY = window.scrollY;

		// Define the IDs of the sections you want to spy on
		const sectionIds = ['scrolltop', 'scroll1', 'scroll2', 'scroll3', 'scroll4'];

		// Determine the active section based on scroll position
		let currentSection = 'scrolltop';

		for (const id of sectionIds) {
			const section = document.getElementById(id);
			if (section && section.offsetTop <= scrollY) {
				currentSection = id;
			}
		}

		setActiveSection(currentSection);
	};

	// Add a scroll event listener to the window
	useEffect(() => {
		window.addEventListener('scroll', handleScroll);
		return () => {
			window.removeEventListener('scroll', handleScroll);
		};
	}, []);

	return (
		<div className={`navbar-nav ${isNavbarOpen ? 'open-nav' : ''}`}>
			<button className={`toggle-button-nav ${isNavbarOpen ? 'hidden' : ''}`} onClick={toggleNavbar}>
				â˜°
			</button>
			{isNavbarOpen && (
				<>
					<button className="close-button-nav" onClick={toggleNavbar}>
						X
					</button>
					<ul className='apple'>
						<li className={activeSection === 'scrolltop' ? 'active' : ''}><a href="#scrolltop">HOME</a></li>
						<li className={activeSection === 'scroll1' ? 'active' : ''}><a href="#scroll1">JOIN #TEAMTREES</a></li>
						<li className={activeSection === 'scroll2' ? 'active' : ''}><a href="#scroll2">LEADERBOARD</a></li>
						<li className={activeSection === 'scroll3' ? 'active' : ''}><a href="#scroll3">ARTICLES</a></li>
						<li className={activeSection === 'scroll4' ? 'active' : ''}><a href="#scroll4">SOCIALS</a></li>
						{/* Add more menu items as needed */}
					</ul>
				</>
			)}
		</div>
	);
}

export default Navbar;
