import React from 'react';

const treeCounterStyles = {
  container: {
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
    backdropFilter: 'blur(5px)',
    transition: 'opacity 0.5s ease-in-out',
  },
  count: {
    fontSize: '4rem',
    fontWeight: 'bold',
    color: 'rgb(111, 186, 13)',
  },
  label: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#333',
    marginTop: '10px',
  },
};

function TreeCounter() {
  const treeCount = 432423224;

  return (
    <div className="tree-counter" style={treeCounterStyles.container}>
      <span style={treeCounterStyles.count}>
        {treeCount.toLocaleString()}
      </span>
      <span style={treeCounterStyles.label}>trees planted</span>
    </div>
  );
}

export default TreeCounter;
