import React, { useEffect, useState, useRef } from 'react';
import './TreeCounter.css'; 
import axios from 'axios';
import apiEndpoints from "../config/api";

function TreeCounter() {
  const [count, setCount] = useState(0); // Total count for animation
  const [donationCount, setDonationCount] = useState(0); // Count from donations
  const [totalDonations, setTotalDonations] = useState(0); // Total donation amount
  const [finalCount, setFinalCount] = useState(0); // Total final count
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [animationComplete, setAnimationComplete] = useState(false);
  const [lastFetchTime, setLastFetchTime] = useState(0); // Track last fetch time
  const animationDuration = 2000; // Duration in milliseconds
  const animationRef = useRef(null);
  const startTimeRef = useRef(null);
  const previousDonationCount = useRef(0); // Track previous donation count

  // Function to animate count from 0 to final number
  const animateCount = (timestamp) => {
    if (!startTimeRef.current) {
      startTimeRef.current = timestamp;
    }

    const elapsed = timestamp - startTimeRef.current;
    const progress = Math.min(elapsed / animationDuration, 1);

    const currentCount = Math.floor(progress * finalCount);
    setCount(currentCount);

    if (progress < 1) {
      animationRef.current = requestAnimationFrame(animateCount);
    } else {
      setCount(finalCount);
      setAnimationComplete(true);
      startTimeRef.current = null;
    }
  };

  // Function to start the animation
  const startAnimation = (targetCount) => {
    console.log(`Starting animation from 0 to ${targetCount}`);
    setCount(0);
    setAnimationComplete(false);
    setFinalCount(targetCount);

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }

    animationRef.current = requestAnimationFrame(animateCount);
  };

  // Function to fetch tree count from the server
  const fetchTreeCount = async (forceRefresh = false) => {
    try {
      // Only fetch if it's been more than 5 seconds since last fetch, or forced refresh
      const now = Date.now();
      if (!forceRefresh && now - lastFetchTime < 5000) {
        console.log('Skipping fetch - too soon since last fetch');
        return;
      }

      setLastFetchTime(now);
      setLoading(true);
      console.log('Fetching tree count from:', apiEndpoints.treeCount);

      // Add cache busting to avoid browser cache
      const url = `${apiEndpoints.treeCount}?_t=${now}`;
      const response = await axios.get(url);

      console.log('Tree count response:', response.data);

      if (response.data) {
        // Get the donation count (trees from donations) 
        const fromDonations = response.data.fromDonations || 0;
        const donationsAmount = response.data.totalDonations || 0;

        console.log(`Trees from donations: ${fromDonations}, Total donations: ${donationsAmount} INR`);

        // Check if the count has changed
        const countChanged = fromDonations !== previousDonationCount.current;
        console.log(`Previous count: ${previousDonationCount.current}, New count: ${fromDonations}, Changed: ${countChanged}`);

        // Update state with the correct values
        setDonationCount(fromDonations);
        setTotalDonations(donationsAmount);
        previousDonationCount.current = fromDonations;

        // Decide whether to animate
        if (!animationComplete || countChanged) {
          console.log('Starting animation due to new count or initial load');
          startAnimation(fromDonations);
        } else {
          console.log('Count unchanged, not animating');
          setFinalCount(fromDonations);
          setCount(fromDonations);
        }
        setError(null);
      } else {
        console.error('Invalid response format:', response.data);
        setError('Invalid tree count data');
      }
    } catch (error) {
      console.error('Error fetching tree count:', error);
      setError('Could not update tree count');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Start by fetching the initial tree count
    fetchTreeCount(true);

    // Set an interval to fetch the updated tree count every 10 seconds
    const intervalId = setInterval(() => {
      fetchTreeCount(true);
    }, 10000); // Update every 10 seconds

    // Clean up
    return () => {
      clearInterval(intervalId);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []); // Empty dependency array so this runs only once when the component mounts

  // Manual refresh button handler
  const handleRefresh = () => {
    fetchTreeCount(true);
  };

  return (
    <div className="tree-counter-container">
      {loading && !animationComplete && <div className="tree-counter-loading">Loading trees...</div>}

      <div className="tree-counter-main">
        <span className="tree-counter-count" title="Trees planted from donations">
          {count}
        </span>
        <span className="tree-counter-label">Trees Planted</span>
      </div>

      {donationCount > 0 && (
        <div className="tree-counter-details">
          <div className="tree-counter-detail-item">
            <span className="detail-label">From Donations:</span>
            <span className="detail-value">{donationCount} trees</span>
          </div>
          <div className="tree-counter-detail-item">
            <span className="detail-label">Donation Amount:</span>
            <span className="detail-value">{totalDonations.toLocaleString()} INR</span>
          </div>
        </div>
      )}

      {error && <span className="tree-counter-error">{error}</span>}

      <button onClick={handleRefresh} className="tree-counter-refresh">
        â†» Refresh
      </button>
    </div>
  );
}

export default TreeCounter;
