import React, { useState } from 'react';
import './DonationForm.css'; // Import your CSS file for styling
import axios from 'axios'

function App() {
	const [selectedAmount, setSelectedAmount] = useState(null);
	const [customAmount, setCustomAmount] = useState('');
	const [showPopup, setShowPopup] = useState(false);

	const handleAmountSelection = (amount) => {
		setSelectedAmount(amount);
	};

	const handleCustomAmountChange = (e) => {
		setCustomAmount(e.target.value);
	};

	const handleNextClick = () => {
		if (customAmount !== '') {
			console.log('adf')
		}
		setShowPopup(true);
	};

	const handlePopupClose = () => {
		setShowPopup(false);
	};

	const handleSignup = async (e) => {
		e.preventDefault();
		try {
			await axios.post('http://localhost:5000/signup', {selectedAmount});
			alert('Success');
			// setUsername('');
		}
		catch (error) {
			console.error('Error');
			alert('An error occurred.');
		}
	};

	return (
		<div className="App">
			<div className="payment-window" id="scroll1">
				<h2>#teamtrees</h2>
				<div className="amount-selection">
					<div className="toggle-buttons">

						<button
							className={`toggle-button ${selectedAmount === 100 ? 'selected' : ''}`}
							onClick={() => handleAmountSelection(100)}
						>
							100rs
						</button>

						<button
							className={`toggle-button ${selectedAmount === 200 ? 'selected' : ''}`}
							onClick={() => handleAmountSelection(200)}
						>
							200rs
						</button><br></br>

						<button
							className={`toggle-button ${selectedAmount === 300 ? 'selected' : ''}`}
							onClick={() => handleAmountSelection(300)}
						>
							300rs
						</button>

						<button
							className={`toggle-button ${selectedAmount === 400 ? 'selected' : ''}`}
							onClick={() => handleAmountSelection(400)}
						>
							400rs
						</button>

						<button
							className={`other-amount-button ${selectedAmount === null ? 'selected' : ''}`}
							onClick={() => handleAmountSelection(null)}
						>
							Other Amount
						</button>

					</div>
					{selectedAmount === null && (
						<input
							type="text"
							placeholder="Enter custom amount"
							value={customAmount}
							className='amount-sel-textbox'
							onChange={handleCustomAmountChange}
						/>
					)}
				</div>
				<button className="next-button" onClick={handleNextClick}>
					Next
				</button>
			</div>

			{showPopup && (
				<div className="popup">
					<div className="popup-content">
						<h3 className='popup-content-heading'>Enter User Details</h3>
						<form className='form1' onSubmit={handleSignup}>
							{/* User details form inputs */}
							<label className='popup-label-name' for="name">DISPLAY NAME:</label>
							<input type="text" placeholder="Name" id="name" className='popup-textbox' />

							<label className='popup-label-name' for="email">EMAIL:</label>
							<input type="email" placeholder="Email" id="email" className='popup-textbox' />

							<label className='popup-label-name' for="phn">PHONE NUMBER:</label>
							<input type="tel" placeholder="Phone Number" id="phn" className='popup-textbox' />

							<label className='popup-label-name' for="msg">MESSAGE:</label>
							<input type="text" placeholder="Message" id="msg" className='popup-textbox' />

							<div className='popup-label'>
								<label className='popup-label-check' for="updates1"><div style={{ display: 'flex' }}>
									<div><input type="checkbox" id="updates1" className='popup-checkbox' /></div>
									<div style={{ marginLeft: '2%' }}><span style={{ color: 'green' }}>YES!</span> I want periodic updates on #TeamTrees</div></div>
								</label>

								<label className='popup-label-check' for="updates2"><div style={{ display: 'flex' }}>
									<div><input type="checkbox" id="updates2" className='popup-checkbox' /></div>
									<div style={{ marginLeft: '2%' }}><span>Please keep my donation anonymous</span></div></div>
								</label>

								<label className='popup-label-check' for="updates3"><div style={{ display: 'flex' }}>
									<div><input type="checkbox" id="updates3" className='popup-checkbox' /></div>
									<div style={{ marginLeft: '2%' }}><span>Send a certificate to your email?(example)</span></div></div>
								</label>
							</div>

							<button type="submit" className='submit-button'>Submit</button>
						</form>

						<button className="close-button" onClick={handlePopupClose}>
							Close
						</button>
					</div>
				</div>
			)}
		</div>
	);
}

export default App;
