import React from 'react';
import './Header.css';
import model3d from '../assets/final_island_2.glb'

function Header() {
  const modelViewerRef = useRef(null);

  useEffect (() => {
    const orbitCycle = [
      '45deg 55deg 4m',
      '-60deg 110deg 2m',
      modelViewerRef.current.cameraOrbit,
    ];

    const intervalId = setInterval(() => {
      const currentOrbitIndex = orbitCycle.indexOf(
        modelViewerRef.current.cameraOrbit
      );
      modelViewerRef.current.cameraOrbit =
        orbitCycle[(currentOrbitIndex + 1) % orbitCycle.length];
    }, 3000);

    return () => clearInterval(intervalId); // Clean up the interval on component unmount
  }, []);

  return (
    <header>
      <model-viewer alt="3d model" src={model3d} camera-controls disable-zoom></model-viewer>

    </header>
  );
}

export default Header;
