import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

// Main Window Component
function MainWindow({ onOpenPopup }) {
  const [selectedAmount, setSelectedAmount] = useState(null);
  const [customAmount, setCustomAmount] = useState('');

  const handleAmountSelection = (amount) => {
    setSelectedAmount(amount);
  };

  const handleCustomAmountChange = (e) => {
    setCustomAmount(e.target.value);
  };

  const handleNextClick = () => {
    onOpenPopup();
  };

  return (
    <div className="payment-window">
      <h2>#teamtrees</h2>
      <div className="amount-selection">
        <div className="toggle-buttons">
          {/* Toggle buttons here */}
        </div>
        {selectedAmount === null && (
          <input
            type="text"
            placeholder="Enter custom amount"
            value={customAmount}
            onChange={handleCustomAmountChange}
          />
        )}
      </div>
      <button className="next-button" onClick={handleNextClick}>
        Next
      </button>
    </div>
  );
}

// Pop-up Component
function Popup({ onClosePopup }) {
  return (
    <div className="popup">
      <div className="popup-content">
        <h3>Enter User Details</h3>
        <form>
          {/* User details form inputs */}
          <input type="text" placeholder="Name" />
          <input type="email" placeholder="Email" />
          <input type="tel" placeholder="Phone Number" />
          <input type="text" placeholder="Display Name" />
          {/* Add more form fields as needed */}
          <button type="submit">Submit</button>
        </form>
        <button className="close-button" onClick={onClosePopup}>
          Close
        </button>
      </div>
    </div>
  );
}

function App() {
  const [showPopup, setShowPopup] = useState(false);

  const handleOpenPopup = () => {
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className="App">
      <MainWindow onOpenPopup={handleOpenPopup} />

      {showPopup && <Popup onClosePopup={handleClosePopup} />}
    </div>
  );
}

export default App;
