import React, { useState } from 'react';
import './DonationForm';

function PaymentWindow({ onPaymentComplete }) {
  const [selectedAmount, setSelectedAmount] = useState(null);
  const [customAmount, setCustomAmount] = useState('');

  const handleAmountSelection = (amount) => {
    setSelectedAmount(amount);
  };

  const handleCustomAmountChange = (e) => {
    setCustomAmount(e.target.value);
  };

  const handleNextClick = () => {
    onPaymentComplete(selectedAmount);
  };

  return (
    <div className="payment-window">
      <h2>#teamtrees</h2>
      <div className="amount-selection">
        <div className="toggle-buttons">
          {/* ... Toggle buttons and custom amount input */}
        </div>
      </div>
      <button className="next-button" onClick={handleNextClick}>
        Next
      </button>
    </div>
  );
}

function UserDetailsForm({ onClose }) {
  return (
    <div className="popup">
      <div className="popup-content">
        <h3>Enter User Details</h3>
        <form>
          {/* User details form inputs */}
          {/* ... */}
          <button type="submit">Submit</button>
        </form>
        <button className="close-button" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
}

function App() {
  const [showUserDetailsForm, setShowUserDetailsForm] = useState(false);

  const handlePaymentComplete = (selectedAmount) => {
    setShowUserDetailsForm(true);
  };

  const handleCloseUserDetailsForm = () => {
    setShowUserDetailsForm(false);
  };

  return (
    <div className="App">
      <PaymentWindow onPaymentComplete={handlePaymentComplete} />

      {showUserDetailsForm && (
        <UserDetailsForm onClose={handleCloseUserDetailsForm} />
      )}
    </div>
  );
}

export default App;
