// src/components/Navbar.js
import React, { useState, useEffect } from 'react';
import './Navbar.css'; // Import your CSS for styling

function Navbar() {
	const [isNavbarOpen, setIsNavbarOpen] = useState(false);
	const [isScrolled, setIsScrolled] = useState(false);

	useEffect(() => {
		const handleScroll = () => {
			setIsScrolled(window.scrollY > 50);
		};

		window.addEventListener('scroll', handleScroll);
		return () => window.removeEventListener('scroll', handleScroll);
	}, []);

	const toggleNavbar = () => {
		setIsNavbarOpen(!isNavbarOpen);
	};

	const closeNavbar = () => {
		setIsNavbarOpen(false);
	};

	const scrollToSection = (sectionId) => {
		const element = document.getElementById(sectionId);
		if (element) {
			element.scrollIntoView({ behavior: 'smooth' });
			closeNavbar();
		}
	};

	return (
		<nav className={`navbar ${isScrolled ? 'scrolled' : ''} ${isNavbarOpen ? 'open' : ''}`}>
			<div className="navbar-container">
				<button 
					className={`navbar-toggle ${isNavbarOpen ? 'open' : ''}`} 
					onClick={toggleNavbar}
					aria-label="Toggle navigation menu"
				>
					<span></span>
					<span></span>
					<span></span>
				</button>

				<div className={`navbar-menu ${isNavbarOpen ? 'open' : ''}`}>
					<ul className="navbar-list">
						<li>
							<button onClick={() => scrollToSection('scrolltop')}>
								HOME
							</button>
						</li>
						<li>
							<button onClick={() => scrollToSection('scroll1')}>
								JOIN #TEAMTREES
							</button>
						</li>
						<li>
							<button onClick={() => scrollToSection('scroll2')}>
								LEADERBOARD
							</button>
						</li>
						<li>
							<button onClick={() => scrollToSection('scroll3')}>
								ARTICLES
							</button>
						</li>
						<li>
							<button onClick={() => scrollToSection('scroll4')}>
								SOCIALS
							</button>
						</li>
					</ul>
				</div>
			</div>
		</nav>
	);
}

export default Navbar;
