import React from 'react';

const treeCounterStyles = {
  container: {
    textAlign: 'center',
    padding: '10px',
    fontFamily: 'Arial, sans-serif',
    background: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.2s',
    cursor: 'pointer',
    userSelect: 'none',
    display: 'inline-block',
    marginBottom:"5%"
  },
  count: {
    fontSize: '4rem',
    fontWeight: 'bold',
    color: 'rgb(111, 186, 13)',
    display: 'block',
    animation: 'bounce 1s infinite',
    paddingBottom:"5px"
  },
  label: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#333',
    opacity: '0.7',
    marginTop: '10px',
  },
  '@keyframes bounce': {
    '0%, 100%': {
      transform: 'translateY(0)',
    },
    '50%': {
      transform: 'translateY(-10px)',
    },
  },
};

function TreeCounter() {
  const treeCount = 432423224;

  return (
    <div style={treeCounterStyles.container}>
      <span style={treeCounterStyles.count}>
        {treeCount.toLocaleString()}
      </span>
      <span style={treeCounterStyles.label}>Trees Tlanted</span>
    </div>
  );
}

export default TreeCounter;
