// src/components/Navbar.js
import React, { useState } from 'react';
import './Navbar.css'; // Import your CSS for styling

function Navbar() {
	const [isNavOpen, setIsNavOpen] = useState(false);

	const toggleNav = () => {
		setIsNavOpen(!isNavOpen);
	};

	const closeNav = () => {
		setIsNavOpen(false);
	};

	const scrollToSection = (sectionId) => {
		const element = document.getElementById(sectionId);
		if (element) {
			element.scrollIntoView({ behavior: 'smooth' });
			closeNav();
		}
	};

	return (
		<div>
			<button 
				className={`toggle-button-nav ${isNavOpen ? 'hidden' : ''}`} 
				onClick={toggleNav}
			>
				☰
			</button>

			<nav className={`navbar-nav ${isNavOpen ? 'open-nav' : ''}`}>
				<button className="close-button-nav" onClick={closeNav}>✕</button>
				<ul className="apple">
					<li>
						<a onClick={() => scrollToSection('scrolltop')}>
							HOME
						</a>
					</li>
					<li>
						<a onClick={() => scrollToSection('scroll1')}>
							JOIN #TEAMTREES
						</a>
					</li>
					<li>
						<a onClick={() => scrollToSection('scroll2')}>
							LEADERBOARD
						</a>
					</li>
					<li>
						<a onClick={() => scrollToSection('scroll3')}>
							ARTICLES
						</a>
					</li>
					<li>
						<a onClick={() => scrollToSection('scroll4')}>
							SOCIALS
						</a>
					</li>
				</ul>
			</nav>
		</div>
	);
}

export default Navbar;
