import React, { useState } from "react";
import "./DonationForm.css"; // Import your CSS file for styling
import axios from "axios";

// import emailjs from 'emailjs-com';

function DonationForm() {
  const [selectedAmount, setSelectedAmount] = useState(null);
  const [customAmount, setCustomAmount] = useState("");
  const [showPopup, setShowPopup] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    message: "",
    updates: false,
    anonymous: false
  });
  const [formErrors, setFormErrors] = useState({});

  const handleAmountSelection = (amount) => {
    setSelectedAmount(amount);
    setCustomAmount("");
  };

  const handleCustomAmountChange = (e) => {
    setCustomAmount(e.target.value);
    setSelectedAmount(null);
  };

  const handleNextClick = () => {
    if ((customAmount !== "" && customAmount !== "0") || selectedAmount !== null) {
      setShowPopup(true);
    }
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const validateForm = () => {
    const errors = {};
    const amount = selectedAmount ? selectedAmount : customAmount;

    if (!formData.name.trim()) {
      errors.name = "Name is required";
    }

    if (!formData.email.trim()) {
      errors.email = "Email is required";
    } else if (!/^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}$/i.test(formData.email)) {
      errors.email = "Invalid email address";
    }

    if (!formData.phone.trim()) {
      errors.phone = "Phone number is required";
    } else if (!/^\d{10}$/i.test(formData.phone)) {
      errors.phone = "Invalid phone number";
    }

    if (!amount || amount <= 0) {
      errors.amount = "Donation amount is required and must be positive";
    }

    return errors;
  };

  const handleDonation = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    try {
      // TODO: Replace with actual API call when backend is ready
      console.log("Form submitted:", {
        amount: selectedAmount || customAmount,
        ...formData
      });
      
      alert("Thank you for your donation! You will receive a certificate soon.");
      setShowPopup(false);
      setFormData({
        name: "",
        email: "",
        phone: "",
        message: "",
        updates: false,
        anonymous: false
      });
    } catch (error) {
      console.error("Error submitting donation:", error);
      alert("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="donation-section" id="scroll1">
      <div className="payment-window">
        <h2 className="donation-title">#teamtrees</h2>
        <h2 className="donation-rate">100 ₹ = 1 ♣</h2>
        
        <div className="amount-selection">
          <div className="toggle-buttons">
            {[100, 200, 300, 400].map((amount) => (
              <button
                key={amount}
                className={`toggle-button ${selectedAmount === amount ? "selected" : ""}`}
                onClick={() => handleAmountSelection(amount)}
              >
                {amount}rs
              </button>
            ))}
            <button
              className={`other-amount-button ${selectedAmount === null ? "selected" : ""}`}
              onClick={() => handleAmountSelection(null)}
            >
              Other Amount
            </button>
          </div>

          {selectedAmount === null && (
            <input
              type="number"
              placeholder="Enter custom amount"
              value={customAmount}
              className="amount-sel-textbox"
              onChange={handleCustomAmountChange}
            />
          )}
          {formErrors.amount && <p className="error-message">{formErrors.amount}</p>}
        </div>

        <button className="next-button" onClick={handleNextClick}>
          Next
        </button>
      </div>

      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <button className="close-button" onClick={handlePopupClose}>×</button>
            <div className="popup-content">
              <h3>Enter User Details</h3>
              <form onSubmit={handleDonation}>
                <div className="form-group">
                  <label htmlFor="name">Display Name:</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="Your name"
                  />
                  {formErrors.name && <p className="error-message">{formErrors.name}</p>}
                </div>

                <div className="form-group">
                  <label htmlFor="email">Email:</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="your@email.com"
                  />
                  {formErrors.email && <p className="error-message">{formErrors.email}</p>}
                </div>

                <div className="form-group">
                  <label htmlFor="phone">Phone Number:</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="10-digit number"
                  />
                  {formErrors.phone && <p className="error-message">{formErrors.phone}</p>}
                </div>

                <div className="form-group">
                  <label htmlFor="message">Message:</label>
                  <input
                    type="text"
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="Your message (optional)"
                  />
                </div>

                <div className="checkbox-group">
                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      name="updates"
                      checked={formData.updates}
                      onChange={handleInputChange}
                    />
                    <span className="checkbox-text">
                      <span className="highlight">YES!</span> I want periodic updates on #TeamTrees
                    </span>
                  </label>

                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      name="anonymous"
                      checked={formData.anonymous}
                      onChange={handleInputChange}
                    />
                    <span className="checkbox-text">Please keep my donation anonymous</span>
                  </label>
                </div>

                <div className="certificate-notice">
                  <span className="highlight">You will receive a certificate soon!</span>
                </div>

                <button type="submit" className="submit-button">
                  Donate Now
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DonationForm;
