import React from 'react';

function AllArticles({ articlesData }) {
  return (
    <div className="all-articles">
      <h1 className="article-h1">All Articles</h1>
      <div className="article-container">
        {articlesData.map((article, index) => (
          <div className="article" key={index}>
            <span className="article-heading">{article.heading}</span>
            <span className="article-location">
              <strong>Location:</strong> {article.location}
            </span>
            <span className="article-trees-planted">
              Trees Planted: {article.treesPlanted}
            </span>
            <span className="article-image">
              <img src={article.imageSrc} alt={article.heading} />
            </span>
            <span className="article-details">{article.details}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AllArticles;
