import React from 'react';
import './Articles.css';

function Articles() {
	const articlesData = [
		{
			heading: 'Article 1',
			location: 'Location 1',
			treesPlanted: 100,
			imageSrc: 'article1.jpg',
			details: 'She had always loved trees. She would spend hours in the forest, listening to the whispers of the leaves and feeling the warmth of the sun. She felt a connection with them, a bond that no one else understood.But then the fire came. It swept through the forest, devouring everything in its path. She watched in horror as her friends burned, their screams echoing in her ears. She ran, but it was too late. The fire caught up with her, and she felt a searing pain.',
		},
		{
			heading: 'Article 2',
			location: 'Location 2',
			treesPlanted: 200,
			imageSrc: 'article2.jpg',
			details: 'Were humming down the Pacific Coast Highway just north of San Francisco on our gap year in a rented real American cherry red 1993 Ford Mustang, all youth and chrome and horsepower and freedom, and youre wearing a red and white candy striped top that makes you look like a barbers poll when you twirl, dappled California sunshine dancing in your hair like fireflies, I kiss your sea salt lips and you flash me a smile that still shimmers through the long shadow of intervening years, one that keeps drawing me back, unable to forget, and unable to move on.',
		},
		{
			heading: 'Article 1',
			location: 'Location 1',
			treesPlanted: 100,
			imageSrc: 'article1.jpg',
			details: 'She had always loved trees. She would spend hours in the forest, listening to the whispers of the leaves and feeling the warmth of the sun. She felt a connection with them, a bond that no one else understood.But then the fire came. It swept through the forest, devouring everything in its path. She watched in horror as her friends burned, their screams echoing in her ears. She ran, but it was too late. The fire caught up with her, and she felt a searing pain.',
		},
		{
			heading: 'Article 2',
			location: 'Location 2',
			treesPlanted: 200,
			imageSrc: 'article2.jpg',
			details: 'Were humming down the Pacific Coast Highway just north of San Francisco on our gap year in a rented real American cherry red 1993 Ford Mustang, all youth and chrome and horsepower and freedom, and youre wearing a red and white candy striped top that makes you look like a barbers poll when you twirl, dappled California sunshine dancing in your hair like fireflies, I kiss your sea salt lips and you flash me a smile that still shimmers through the long shadow of intervening years, one that keeps drawing me back, unable to forget, and unable to move on.',
		},
	];

	return (
		<div className="articles">
			<h1 className='article-h1'>ARTICLES</h1>
			<div className="article-container">
				{articlesData.map((article, index) => (
					<div className="article" key={index}>
						<span></span>
						<img src={article.imageSrc} alt={article.heading} />
						<h3>{article.heading}</h3>
						<p><strong>Location:</strong> {article.location}</p>
						<span className="article-trees-planted">Trees Planted:{article.treesPlanted}</span>
						<p>{article.details}</p>
					</div>
				))}
			</div>

			<h1 className='article-h1'>LEADERBOARD</h1>
			<ul>
				{leaderboardData.map((entry, index) => (
					<li key={index}>
						<div className="leaderboard-entry-details">
							<div className="leaderboard-entry-logo">Logo</div>
							<div className='leaderboard-entry-user'>
								<span className="leaderboard-entry-name">{entry.name}</span><br></br>
								<span className="leaderboard-entry-comment">"{entry.comment}"</span>
							</div>
							<span className="leaderboard-entry-amount">{entry.amount} Trees</span>

						</div>
					</li>
				))}
			</ul>
		</div>
	);
}

export default Articles;
