import React, { useState } from "react";
import axios from "axios";

const DonationForm = () => {
  const [selectedAmount, setSelectedAmount] = useState(null);
  const [customAmount, setCustomAmount] = useState("");
  const [Fname, setFname] = useState("");
  const [Femail, setFemail] = useState("");
  const [Fphone, setFphone] = useState("");
  const [Fmsg, setFmsg] = useState("");
  const [check1, setCheck1] = useState(false);
  const [check2, setCheck2] = useState(false);
  const [errors, setErrors] = useState({});

  // Amount selection logic
  const amount = selectedAmount ? selectedAmount : customAmount;

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Reset errors before validation
    setErrors({});

    // Validate the form fields
    const validationErrors = {};

    if (Fname.trim() === "") {
      validationErrors.fname = "Name is required";
    }

    if (Femail.trim() === "") {
      validationErrors.Femail = "Email is required";
    } else if (!/^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}$/i.test(Femail)) {
      validationErrors.Femail = "Invalid email address";
    }

    if (Fphone.trim() === "") {
      validationErrors.Fphone = "Phone number is required";
    } else if (!/^\d{10}$/i.test(Fphone)) {
      validationErrors.Fphone = "Invalid phone number";
    }

    if (!amount || amount <= 0) {
      validationErrors.amount = "Donation amount is required and must be positive";
    }

    // Check for validation errors
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return; // Stop submission if errors exist
    }

    // If no errors, send data to the server
    try {
      const response = await axios.post("http://localhost:4000/donateAmount", {
        amount,
        Fname,
        Femail,
        Fphone,
        Fmsg,
        check1,
        check2,
      });

      if (response.data.message) {
        alert(response.data.message);
        // Reset form
        setFname("");
        setFemail("");
        setFphone("");
        setFmsg("");
        setSelectedAmount(null);
        setCustomAmount("");
        setCheck1(false);
        setCheck2(false);
      }
    } catch (error) {
      console.error("Error submitting the donation form:", error);
      alert("There was an error processing your donation.");
    }
  };

  return (
    <div>
      <h1>Donate to Team Trees</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={Fname}
            onChange={(e) => setFname(e.target.value)}
            placeholder="Enter your name"
          />
          {errors.fname && <p>{errors.fname}</p>}
        </div>

        <div>
          <label>Email:</label>
          <input
            type="email"
            value={Femail}
            onChange={(e) => setFemail(e.target.value)}
            placeholder="Enter your email"
          />
          {errors.Femail && <p>{errors.Femail}</p>}
        </div>

        <div>
          <label>Phone:</label>
          <input
            type="text"
            value={Fphone}
            onChange={(e) => setFphone(e.target.value)}
            placeholder="Enter your phone number"
          />
          {errors.Fphone && <p>{errors.Fphone}</p>}
        </div>

        <div>
          <label>Donation Amount:</label>
          <select
            value={selectedAmount}
            onChange={(e) => setSelectedAmount(e.target.value)}
          >
            <option value="">Select Amount</option>
            <option value="10">10</option>
            <option value="50">50</option>
            <option value="100">100</option>
          </select>
          <input
            type="number"
            value={customAmount}
            onChange={(e) => setCustomAmount(e.target.value)}
            placeholder="Enter custom amount"
          />
          {errors.amount && <p>{errors.amount}</p>}
        </div>

        <div>
          <label>Message:</label>
          <textarea
            value={Fmsg}
            onChange={(e) => setFmsg(e.target.value)}
            placeholder="Enter your message (Optional)"
          ></textarea>
        </div>

        <div>
          <label>
            <input
              type="checkbox"
              checked={check1}
              onChange={() => setCheck1(!check1)}
            />
            Receive Updates
          </label>
        </div>

        <div>
          <label>
            <input
              type="checkbox"
              checked={check2}
              onChange={() => setCheck2(!check2)}
            />
            Remain Anonymous
          </label>
        </div>

        <button type="submit">Donate</button>
      </form>
    </div>
  );
};

export default DonationForm;
