import React from 'react';
import './Leaderboard.css';
import transitionhill from '../assets/transition-hill.png';

function Leaderboard() {
    // Dummy data for leaderboard
    const dummyData = [
        {
            name: "John Doe",
            message: "Let's make the world greener!",
            amount: 5000
        },
        {
            name: "Jane Smith",
            message: "Every tree counts!",
            amount: 3000
        },
        {
            name: "Mike Johnson",
            message: "Saving the planet one tree at a time",
            amount: 2500
        },
        {
            name: "Sarah Williams",
            message: "For a better future",
            amount: 2000
        },
        {
            name: "David Brown",
            message: "Together we can make a difference",
            amount: 1500
        }
    ];

    return (
        <div id="scroll2" className="leaderboard-section">
            <div className="leaderboard">
                <h1 className="leaderboard-h1">LEADERBOARD</h1>
                <div className="leaderboard-container">
                    <ul>
                        {dummyData.map((entry, index) => (
                            <li key={index} className="leaderboard-item">
                                <div className="leaderboard-entry-details">
                                    <div className="leaderboard-entry-logo">ðŸŒ³</div>
                                    <div className="leaderboard-entry-user">
                                        <span className="leaderboard-entry-name">
                                            {entry.name}
                                        </span>
                                        <br />
                                        <span className="leaderboard-entry-comment">
                                            "{entry.message}"
                                        </span>
                                    </div>
                                    <span className="leaderboard-entry-amount">
                                        {entry.amount} Trees
                                    </span>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <img
                src={transitionhill}
                className="transition-hill"
                alt="Decorative Hill"
            />
        </div>
    );
}

export default Leaderboard;
